var ComponentsBootstrapMaxlength = function () {
    var a = function () {
        $("#maxlength_defaultconfig").maxlength({limitReachedClass: "label label-danger"}), $("#maxlength_thresholdconfig").maxlength({
            limitReachedClass: "label label-danger",
            threshold: 20
        }), $("#maxlength_alloptions").maxlength({
            alwaysShow: !0,
            warningClass: "label label-success",
            limitReachedClass: "label label-danger",
            separator: " out of ",
            preText: "You typed ",
            postText: " chars available.",
            validate: !0
        }), $("#maxlength_textarea").maxlength({
            limitReachedClass: "label label-danger",
            alwaysShow: !0
        }), $("#maxlength_placement").maxlength({
            limitReachedClass: "label label-danger",
            alwaysShow: !0,
            placement: App.isRTL() ? "top-right" : "top-left"
        })
    };
    var b = function (ids) {
        ids.forEach(function (id) {
            $("#" + id).maxlength({
                limitReachedClass: "label label-danger",
                alwaysShow: !0
            })
        });
    };
    return {
        init: function (ids) {
            if (ids != null) {
                b(ids);
            }
            else {
                a();
            }

        }
    }
}();
jQuery(document).ready(function () {
    ComponentsBootstrapMaxlength.init()
});